mapper.select:
SELECT COUNT(*) AS TOTCNT FROM ( SELECT DATA_ID, GROUP_ID, AWARDS, AUTHOR, SUBJECT, CONTENT, WRITER, dbo.SF_USER_NM(WRITER) WRITER_NM, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT FROM CMS_AWARDS_DATA WHERE GROUP_ID=#{group_id} AND IS_NOTICE='N'
AND ${searchCondition} LIKE '%' + #{searchKeyword} + '%'
) INNB
