mapper.update:

UPDATE ${tbl_nm} SET

		CTGRY_IDX = #{ctgry_idx},
		SUBJECT = #{subject},
		BRD_CONT = #{brd_cont},

	MODIFIER = #{modifier},
	MDT = GETDATE(),
	FAQ_TYPE = #{faq_type}
WHERE
	BRD_ID = #{brd_id} 
	AND CONT_IDX = #{cont_idx}

mapper.delete:

DELETE FROM
	${tbl_nm}
WHERE
	BRD_ID = #{brd_id} AND
	CONT_IDX = #{cont_idx}

mapper.select:
SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CONT_IDX DESC) rn, TB.* FROM (
SELECT BRD_ID, CONT_IDX, CTGRY_IDX, dbo.SF_CTGRY_NM(CTGRY_IDX) CTGRY_NM, SUBJECT, BRD_CONT, HITS, A.WRITER, CONVERT(VARCHAR(10), A.WDT, 20) WDT, A.MODIFIER, CONVERT(VARCHAR(10), A.MDT, 20) MDT, ISNULL(dbo.SF_USER_NM(WRITER), 'GUEST') WRITER_NM, ROUND(DATEDIFF(DD,A.WDT,GETDATE()),0) DFR_WDT FROM ${tbl_nm} A WHERE BRD_ID = #{brd_id}
AND FAQ_TYPE = 'A'
AND (SUBJECT LIKE '%'+#{faqCont}+'%')
AND
( SUBJECT LIKE '%' + #{searchKeyword} + '%' OR BRD_CONT LIKE '%' + #{searchKeyword} + '%' )
${searchCondition} LIKE '%' + #{searchKeyword} + '%'
AND CTGRY_IDX = #{ctgry_idx}
) TB ) MORG WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

