mapper.update:
UPDATE ${tbl_nm} SET
HITS = #{hits},
MDT = MDT WHERE BRD_ID = #{brd_id} AND CONT_IDX = #{cont_idx}

mapper.delete:
DELETE FROM ${tbl_nm}
WHERE
	BRD_ID = #{brd_id} AND
	CONT_IDX = #{cont_idx}

mapper.select:
SELECT COUNT(*) AS TOTCNT FROM ( SELECT A.*, dbo.SF_USER_NM(WRITER) WRITER_NM FROM ${tbl_nm} A ) A WHERE BRD_ID = #{brd_id}
AND
( SUBJECT LIKE '%' + #{searchKeyword} + '%' OR BRD_CONT LIKE '%' + #{searchKeyword} + '%' )
${searchCondition} LIKE '%' + #{searchKeyword} + '%'
AND CTGRY_IDX = #{srch_ctgry_idx}
