SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY STAT_DT DESC) rn, TB.* FROM (
SELECT STAT_KEY, STAT_SESSID, STAT_KEY_NM, STAT_CITY, STAT_NAT, STAT_NAT_CD, STAT_OS, STAT_BW, STAT_REFERER, STAT_DT, STAT_RADDR, STAT_UID, STAT_UNM FROM ( SELECT STAT_KEY, STAT_SESSID, STAT_KEY_NM, UPPER(STAT_CITY) STAT_CITY, UPPER(STAT_NAT) STAT_NAT, STAT_NAT_CD, STAT_OS, STAT_BW, STAT_REFERER, CONVERT(VARCHAR(19), STAT_DT, 20) STAT_DT, STAT_RADDR, STAT_UID, dbo.SF_USER_NM(STAT_UID) STAT_UNM FROM CMS_COMM_STAT ) INNA WHERE 1 = 1
AND STAT_KEY IN ('USERMAN', 'APPLMAN')
AND STAT_KEY = #{stat_key}
AND STAT_DT BETWEEN #{srchFromDT} || ' 00:00:00' AND #{srchToDT} || ' 23:59:59'
) TB ) MORG WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY STAT_DT DESC) rn, TB.* FROM (
SELECT STAT_KEY, STAT_SESSID, STAT_KEY_NM, STAT_CITY, STAT_NAT, STAT_NAT_CD, STAT_OS, STAT_BW, STAT_REFERER, STAT_DT, STAT_RADDR, STAT_UID, STAT_UNM FROM ( SELECT STAT_KEY, STAT_SESSID, STAT_KEY_NM, UPPER(STAT_CITY) STAT_CITY, UPPER(STAT_NAT) STAT_NAT, STAT_NAT_CD, STAT_OS, STAT_BW, STAT_REFERER, CONVERT(VARCHAR(19), STAT_DT, 20) STAT_DT, STAT_RADDR, STAT_UID, dbo.SF_USER_NM(STAT_UID) STAT_UNM FROM CMS_COMM_STAT ) INNA WHERE 1 = 1
AND STAT_KEY IN ('USERMAN', 'APPLMAN')
AND STAT_KEY = #{stat_key}
AND STAT_DT BETWEEN #{srchFromDT} || ' 00:00:00' AND #{srchToDT} || ' 23:59:59'
) TB ) MORG

SELECT ISNULL(UPPER(STAT_NAT), 'UNKNOWN') CHARTDATA_KEY, COUNT(*) CHARTDATA_VAL FROM CMS_COMM_STAT WHERE STAT_KEY = 'MAIN'
AND CONVERT(VARCHAR(4), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(6), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(8), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
GROUP BY UPPER(STAT_NAT) ORDER BY CHARTDATA_VAL

SELECT ISNULL(UPPER(STAT_CITY), 'UNKNOWN') CHARTDATA_KEY, COUNT(*) CHARTDATA_VAL FROM CMS_COMM_STAT WHERE STAT_KEY = 'MAIN'
AND CONVERT(VARCHAR(4), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(6), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(8), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
GROUP BY UPPER(STAT_CITY) ORDER BY CHARTDATA_VAL

SELECT ISNULL(STAT_OS, 'UNKNOWN') CHARTDATA_KEY, COUNT(*) CHARTDATA_VAL FROM CMS_COMM_STAT WHERE STAT_KEY = 'MAIN'
AND CONVERT(VARCHAR(4), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(6), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(8), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
GROUP BY STAT_OS ORDER BY CHARTDATA_VAL

SELECT ISNULL(STAT_BW, 'UNKNOWN') CHARTDATA_KEY, COUNT(*) CHARTDATA_VAL FROM CMS_COMM_STAT WHERE STAT_KEY = 'MAIN'
AND CONVERT(VARCHAR(4), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(6), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(8), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
GROUP BY STAT_BW ORDER BY CHARTDATA_VAL

WITH DLIST AS ( SELECT 1 AS ADDDT UNION ALL SELECT ADDDT + 1 FROM DLIST WHERE ADDDT <= 23) SELECT '0' AS CHARTDATA_SRZ, B.CTDATE + 'ì‹œ' AS CHARTDATA_KEY, ISNULL(CNT, 0) AS CHARTDATA_VAL FROM ( SELECT STAT_DT, COUNT(*) CNT FROM ( SELECT CONVERT(VARCHAR(2), STAT_DT, 8) STAT_DT FROM CMS_COMM_STAT A WHERE STAT_KEY = 'MAIN'
AND CONVERT(VARCHAR(4), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(6), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
AND CONVERT(VARCHAR(8), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
) INNA GROUP BY STAT_DT ) A RIGHT OUTER JOIN ( SELECT RIGHT(REPLICATE('0', 2) + CAST((ADDDT - 1) AS VARCHAR), 2) AS CTDATE FROM DLIST ) B ON A.STAT_DT = B.CTDATE ORDER BY CTDATE option (MAXRECURSION 365)

SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CHARTDATA_KEY) rn, TB.* FROM (
SELECT STAT_KEY_NM CHARTDATA_KEY, COUNT(*) CHARTDATA_VAL FROM CMS_COMM_STAT WHERE ( STAT_KEY <> 'MAIN' AND STAT_KEY <> 'LOGIN_USR' )
AND CONVERT(VARCHAR(8), STAT_DT, 112) BETWEEN #{srchFromDT} AND #{srchToDT}
GROUP BY STAT_KEY, STAT_KEY_NM
) TB ) MORG
