mapper.update:
UPDATE CMS_USR_CONTENTS SET MU_SITE = #{mu_site}, MU_LANG = #{mu_lang}, UCONT_SUBJECT = #{ucont_subject}, UCONT_DFT = #{ucont_dft}, UCONT_CSS = #{ucont_css}, UCONT_CONT = #{ucont_cont},
REF_MENU_NIX = #{ref_menu_nix},
MODIFIER = #{modifier}, MDT = GETDATE() WHERE UCONT_ID = #{ucont_id}
AND UCONT_VERSION = (SELECT MAX(UCONT_VERSION) FROM CMS_USR_CONTENTS WHERE UCONT_ID = #{ucont_id})
AND UCONT_VERSION = #{ucont_version}

UPDATE CMS_USR_CONTENTS SET
	REF_MENU_NIX = #{ref_menu_nix}
WHERE
	UCONT_ID = #{ucont_id}

mapper.select:
SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, UCONT_CONT, MU_SITE, MU_LANG, REF_MENU_NIX, dbo.SF_MENU_NM(null, REF_MENU_NIX) REF_MENU_NM, WRITER, dbo.SF_USER_NM(WRITER) WRITER_NM, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT FROM CMS_USR_CONTENTS WHERE UCONT_ID = #{ucont_id}
AND UCONT_VERSION = (SELECT MAX(UCONT_VERSION) FROM CMS_USR_CONTENTS WHERE UCONT_ID = #{ucont_id})
AND UCONT_VERSION = #{ucont_version}

select.if:
SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY UCONT_ID ASC) rn, TB.* FROM (
SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, UCONT_CONT, MU_SITE, MU_LANG, REF_MENU_NIX, WRITER, dbo.SF_USER_NM(WRITER) WRITER_NM, WDT, MODIFIER, MDT FROM ( SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, NULL UCONT_CONT, MU_SITE, MU_LANG, ISNULL(REF_MENU_NIX, NULL) REF_MENU_NIX, WRITER, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT, RANK() OVER (PARTITION BY UCONT_ID ORDER BY UCONT_VERSION DESC) RANKORD FROM CMS_USR_CONTENTS WHERE 1 = 1
AND MU_SITE = #{srch_mu_site}
AND MU_LANG = #{srch_mu_lang}
AND
( UCONT_SUBJECT LIKE '%' + #{searchKeyword} + '%' OR UCONT_CONT LIKE '%' + #{searchKeyword} + '%' )
${searchCondition} LIKE '%' + #{searchKeyword} + '%'
) INNA WHERE RANKORD = 1
) TB ) MORG WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.select:
WITH TG ( MENU_IDX, MENU_NIX, MENU_NM, UP_MENU_IDX, MENU_LV, SORT_PATH, USE_YN ) AS ( SELECT MENU_IDX, MENU_NIX, MENU_NM, UP_MENU_IDX, 1 AS MENU_LV, CAST('/' + RIGHT('000000' + CONVERT(VARCHAR, MENU_ORD), 6) AS VARCHAR(MAX)), USE_YN FROM CMS_SYS_MENU WHERE MENU_IDX IN (SELECT dbo.SF_TOP_MENU(REF_MENU_NIX) FROM CMS_USR_CONTENTS WHERE REF_MENU_NIX IS NOT NULL AND REF_MENU_NIX <> '' ) UNION ALL SELECT CHILD.MENU_IDX, CHILD.MENU_NIX, CHILD.MENU_NM, CHILD.UP_MENU_IDX, TG.MENU_LV + 1 AS MENU_LV, TG.SORT_PATH + '/' + RIGHT('000000' + CONVERT(VARCHAR, CHILD.MENU_ORD), 6), CHILD.USE_YN FROM CMS_SYS_MENU AS CHILD, TG WHERE TG.MENU_IDX = CHILD.UP_MENU_IDX ) SELECT ROW_NUMBER() OVER (ORDER BY SORT_PATH ASC), B.MENU_IDX, B.UP_MENU_IDX, B.MENU_NM, B.MENU_LV, B.USE_YN, B.IS_LEAF, UCONT_ID, ISNULL(UCONT_VERSION, 1) UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, UCONT_CONT, A.MU_SITE, A.MU_LANG, REF_MENU_NIX, A.WRITER, dbo.SF_USER_NM(A.WRITER) WRITER_NM, A.WDT, A.MODIFIER, A.MDT FROM (
SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, UCONT_CONT, MU_SITE, MU_LANG, REF_MENU_NIX, WRITER, WDT, MODIFIER, MDT FROM ( SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, NULL UCONT_CONT, MU_SITE, MU_LANG, ISNULL(REF_MENU_NIX, NULL) REF_MENU_NIX, WRITER, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT, RANK() OVER (PARTITION BY UCONT_ID ORDER BY UCONT_VERSION DESC) RANKORD FROM CMS_USR_CONTENTS WHERE 1 = 1
AND MU_SITE = #{srch_mu_site}
AND MU_LANG = #{srch_mu_lang}
AND
( UCONT_SUBJECT LIKE '%' + #{searchKeyword} + '%' OR UCONT_CONT LIKE '%' + #{searchKeyword} + '%' )
${searchCondition} LIKE '%' + #{searchKeyword} + '%'
) INNA WHERE RANKORD = 1 ) A RIGHT OUTER JOIN ( SELECT MENU_IDX, MENU_NIX, MENU_NM, MENU_LV, SORT_PATH, UP_MENU_IDX, CASE (SELECT COUNT(*) FROM CMS_SYS_MENU WHERE UP_MENU_IDX = TG.MENU_IDX) WHEN 0 THEN 1 ELSE 0 END AS IS_LEAF, USE_YN FROM TG ) B ON A.REF_MENU_NIX = B.MENU_NIX

SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY UCONT_VERSION DESC) rn, TB.* FROM (
SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_DFT, UCONT_CSS, UCONT_CONT, MU_SITE, MU_LANG, REF_MENU_NIX, WRITER, dbo.SF_USER_NM(WRITER) WRITER_NM, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT, RANK() OVER (PARTITION BY UCONT_ID ORDER BY UCONT_VERSION DESC) RANKORD FROM CMS_USR_CONTENTS WHERE REF_MENU_NIX IN (${searchQuery})
) TB ) MORG

SELECT COUNT(*) AS TOTCNT FROM ( SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_CSS, NULL UCONT_CONT, MU_LANG, REF_MENU_NIX, WRITER, dbo.SF_USER_NM(WRITER) WRITER_NM, WDT, MODIFIER, MDT FROM ( SELECT UCONT_ID, UCONT_VERSION, UCONT_SUBJECT, UCONT_CSS, UCONT_CONT, MU_LANG, REF_MENU_NIX, WRITER, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT, RANK() OVER (PARTITION BY UCONT_ID ORDER BY UCONT_VERSION DESC) RANKORD FROM CMS_USR_CONTENTS WHERE 1 = 1
AND MU_SITE = #{srch_mu_site}
AND MU_LANG = #{srch_mu_lang}
AND
( UCONT_SUBJECT LIKE '%' + #{searchKeyword} + '%' OR UCONT_CONT LIKE '%' + #{searchKeyword} + '%' )
${searchCondition} LIKE '%' + #{searchKeyword} + '%'
) INNA WHERE RANKORD = 1 ) INNB

mapper.delete:

DELETE FROM CMS_USR_CONTENTS WHERE
	UCONT_ID = #{ucont_id} AND
	UCONT_VERSION  >  #{ucont_version}
