mapper.select:
SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY POP_SORT DESC) rn, TB.* FROM (
SELECT POP_SEQ , POP_TITLE , POP_TYPE , POP_FROM , POP_TO , POP_W , POP_H , POP_CONTENT , POP_IMAGE , POP_LINK , POP_TARGET , USE_YN , WRITER , CONVERT(VARCHAR(19), WDT, 20) WDT , POP_SORT , VCHKCODE FROM CMS_POPUP A LEFT OUTER JOIN ( SELECT * FROM CMS_ATACH WHERE ATCKEY_1ST = 'POPUP' AND ATCKEY_3RD = 1 AND ATCKEY_4TH = 1 AND IS_THUMB = 'N'
) B ON A.POP_SEQ = B.ATCKEY_2ND
) TB ) MORG WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

