mapper.insert:
SELECT LEFT(#{up_auth_cd}, 1) + RIGHT(REPLICATE('0', 4) + CAST(CAST((ISNULL(RIGHT(MAX(AUTH_CD), 4), '0')) + 1 AS INT) AS VARCHAR), 4) AS AUTH_CD FROM CMS_SYS_AUTH WHERE UP_AUTH_CD = #{up_auth_cd}
INSERT INTO CMS_SYS_AUTH ( AUTH_CD, UP_AUTH_CD, AUTH_NM, AUTH_LV, AUTH_ORD, WRITER, WDT ) VALUES ( #{auth_cd}, #{up_auth_cd}, #{auth_nm}, ( SELECT ISNULL(MAX(AUTH_LV), #{auth_lv}) + 1 FROM CMS_SYS_AUTH WHERE UP_AUTH_CD = #{up_auth_cd} ),
( SELECT ISNULL(MAX(AUTH_ORD), 0) + 1 FROM CMS_SYS_AUTH WHERE ( UP_AUTH_CD
IS NULL OR UP_AUTH_CD = ''
= #{up_auth_cd}
) ),
#{auth_ord},
#{writer}, GETDATE() )

mapper.update:
UPDATE CMS_SYS_AUTH SET
	AUTH_ORD = AUTH_ORD + 1
WHERE
	(
		UP_AUTH_CD

			IS NULL OR UP_AUTH_CD = ''


			= #{up_auth_cd}

	) AND
		AUTH_ORD  >=  #{auth_ord}

UPDATE CMS_SYS_AUTH SET
	AUTH_ORD = AUTH_ORD + (${add_ord})
WHERE
	(
		UP_AUTH_CD

			IS NULL OR UP_AUTH_CD = ''


			= #{up_auth_cd}

	) AND
	AUTH_ORD BETWEEN #{from_ord} AND #{to_ord}

mapper.select:
SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY USE_YN DESC, AUTH_LV ASC) rn, TB.* FROM (
SELECT AUTH_CD, UP_AUTH_CD, AUTH_NM, AUTH_ORD, AUTH_LV, USE_YN, WRITER, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT FROM CMS_SYS_AUTH WHERE 1 = 1
AND USE_YN = #{use_yn}
AND LEFT(AUTH_CD, 1) NOT IN ('D')
AND ( UP_AUTH_CD IS NULL OR UP_AUTH_CD = '' )
AND UP_AUTH_CD = #{up_auth_cd}
) TB ) MORG

SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY AUTH_LV) rn, TB.* FROM (
SELECT AUTH_CD, UP_AUTH_CD, AUTH_NM, AUTH_ORD, AUTH_LV, WRITER, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT FROM CMS_SYS_AUTH WHERE AUTH_LV BETWEEN 2 AND 9998 AND USE_YN = 'Y' AND UP_AUTH_CD IS NOT NULL AND UP_AUTH_CD <> ''
) TB ) MORG
