mapper.update:
UPDATE CMS_SYS_CODE SET
	CODE_ORD = CODE_ORD + 1
WHERE
	(
		UP_CODE_IDX

			IS NULL OR UP_CODE_IDX = ''


			= #{up_code_idx}

	) AND
	CODE_ORD  >=  #{code_ord}

UPDATE CMS_SYS_CODE SET
	CODE_ORD = CODE_ORD + (${add_ord})
WHERE
	(
		UP_CODE_IDX

			IS NULL OR UP_CODE_IDX = ''


			= #{up_code_idx}

	) AND
	CODE_ORD  BETWEEN #{from_ord} AND #{to_ord}

mapper.select:
SELECT MORG.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY USE_YN DESC, CODE_ORD ASC) rn, TB.* FROM (
SELECT CODE_IDX, UP_CODE_IDX, CODE_NM, CODE_ORD, USE_YN, WRITER, CONVERT(VARCHAR(19), WDT, 20) WDT, MODIFIER, CONVERT(VARCHAR(19), MDT, 20) MDT FROM CMS_SYS_CODE WHERE 1 = 1 AND USE_YN = #{use_yn}
AND ( UP_CODE_IDX IS NULL OR UP_CODE_IDX = '' )
AND UP_CODE_IDX = #{up_code_idx}
AND CODE_IDX NOT IN (${except_codes})
) TB ) MORG
